function boyut($file){
    if($size = @filesize($file)){
        if($size <= 1024) return $size;
        else{if($size <= 1024*1024) {
            $size = @round($size / 1024,2);
            return "$size kb";
            } else {
                $size = @round($size / 1024 / 1024,2);
                return "$size mb";}}}
else return "???";
} 



